import { socket } from '@/components/game_lobby.vue'
import { default as modal_area } from '@/components/modal_area.vue'
import { default as personal_game_data } from '@/components/personal_game_data.vue' 

export default {
    name : 'game_board',
    created() {
        // 게임 초기화 & 시작 트리거
        socket.emit("created_data_announce");

        //최초 분배된 기본 재료 수령
        socket.on("get_ingame_data", (data) => {
            this.user_data = data;
            for(let i = 0; i < data.length; i++){
                if( data[i].is_master === 'true' ) {
                    this.room_owner = data[i].user_name;
                }
                if( this.my_key == data[i].user_key ){
                    this.my_name = data[i].user_name;
                }
            }

            let msg = {
                speaker : this.my_name,
                msg     : "방에 입장하였습니다.",
                type    : 'announce',
            }
            socket.emit("chat", msg);
        });

        //다른 채팅이 있는지 확인
        socket.on("chat", (data) => {
            // console.log("서버에서 받아온 데이터");
            // console.log(data);
            // console.log("받아오기 끝");
            switch ( data.type ) {
                case "announce" :
                    this.textarea += data.speaker + " 이(가) " + data.msg + "\n";
                    break;
                case "normal" :
                    this.textarea += data.speaker + " : " + data.msg + "\n";
                    break;
            }
            //console.log("chat :: ");
            //console.log(data);
        });

        socket.on("ingredient_select_card_open", (data) => {
            //console.log(data);
            this.ingredient_card_selected.card_1 = data[0];
            this.ingredient_card_selected.card_2 = data[1];
            this.ingredient_card_selected.card_3 = data[2];
            this.ingredient_card_selected.card_4 = data[3];
            this.ingredient_card_selected.card_5 = data[4];
        });

        socket.on("draw_ingredient_card_result", (data) => {
            this.user_data = data;
            //console.log(this.user_data);
        });

    },
    components : {
        modal_component : modal_area,
        personal_game_data : personal_game_data,
    },
    data() {
        return  {
            user_data : '', 
            my_key : socket.id,
            my_name : '',
            room_owner : '',
            total_dicount_adventruer : 4,
            textarea : '',
            message : '',
            ingredient_card_selected : {
                card_1 : '0',
                card_2 : '0',
                card_3 : '0',
                card_4 : '0',
                card_5 : '0',
            },
            decide_order : false,
        }
    },

    methods : {
        use_chat : function(){
            if( this.message !== '' ){
              let msg = {
                speaker : this.my_name,
                msg : this.message,
                type : "normal"
              }
            //   console.log("서버에 보낼 데이터");
            //   console.log(msg);
            //   console.log("보낼준비 끝");
              this.message = '';
              socket.emit("chat", msg);
            }
        },

        draw_ingredient_card : function(cnt) {
            let data = {
                cnt : cnt,
                my_key : this.my_key,
            }
            socket.emit("draw_ingredient_card", data);
        },

        round_ready : function() {
            console.log("ready");
        },

        decide_order_end : function() {
            this.decide_order = true;
        }
    }
}
