      // 라운드 준비 완료 확인 모달
      round_order_check: false,
      // 구매 가능한 아티펙트 변수
      can_buy_artifacts: '',
      // 재료조합 추리 및 결과 테이블
      result_table: [],
      // board 선택을 위한 변수 8개
      board_1_selected: false,
