            if( !is_select_order ) {
                alert("순서를 고르고 결정을 눌러주세요");
                this.decide_order_modal = false;
                return;
            }
            //위 if문에 안걸리면 선택한 순서가 있다는 것 이므로 진행
            // 게임 결정 순서를 공유해야 하므로 서버에 값을 증가시키라고 전송
