import { io } from 'socket.io-client';

// 다른 곳에서도 쓸 수 있게 전역 선언
var socket;

export default {
    name : 'room_list',
    created() {
        socket = io('http://' + this.$route.params.ip + ":3000");
        this.enter_server();        
    },
    mounted() {
        socket.on("enter_room_success", (data)=> {
            // router로 방 선택/ 생성 창으로 이동
            console.log(data);
            
            this.room_list = data;
        });
    },
    data () {
        return {
            name : this.$route.params.name,
            room_list : '',
        }
    },
    methods : {
        enter_server : function() {
            socket.emit("enter_room_gate", this.$route.params.name);
        },
