export function create_socket_server( params ) {

    const server = require('http').createServer(params.room_address);
    const io = require('socket.io')(server);

    io.listen(params.port_number);
    console.log(params);

    io.on('connect', function(socket) {
        console.log("connect on ");
        socket.emit('news', { serverData : "서버 작동" });
        
        socket.on('client login', function (data) {
            console.log(data);
        });
            
        socket.on('disconnect', function(){
            console.log('접속이 종료되었습니다.');
	});
    });
}
