import select_user_mount from '@/components/select_user_mount.vue'

export default {
    name : 'first_page',
    components: {
        select_user_mount
    },
    data () {
        return {
            open_toggle: false,
            toggle_name : '게임 열기',
            is_show : 'inline',
        }
    },
    methods : {
        toggle_open_game: function () {
            this.is_show = this.is_show === 'inline' ? 'none' : 'inline';
            this.toggle_name = this.toggle_name === '게임 열기' ? '뒤로 가기' : '게임 열기';
            return this.open_toggle = this.open_toggle === false ? true : false;
        },
        enter_game : function() {
            this.$router.push({
                name: 'enter_lobby'
            })
        }
    }
}
