import { socket } from "./game_lobby"

export default {
    name : 'end_page',
    mounted () {
        // 다시하기 취소!
        socket.on("restart_cancel_check", () => {
            this.restart_member_count--;
        });

        socket.on("restart", (data) => {
            this.room_pw = data.room_pw;
            this.count = data.count;
            let is_master = false;
            if( data.master_name == this.my_name ){
                is_master = true;
            }
            this.$router.push({
                name: 'game_lobby',
                params : {
                    user_name : this.my_name,
                    room_pw   : this.room_pw,
                    count     : this.count,
                    is_master : is_master,
                    // ip port 정보도 보내주는게 원칙인데 서버에 방 형식으로 구현방식을 변경해서 진행해야할듯 일단 ㄱ
                }
            });
        });
    },
    data () {
        return {
            game_result : this.$route.params.game_result,
            my_key : '',
            restart_member_count : 0,
            cancel_restart_val : false,
            my_name : this.$route.params.my_name,
            room_pw : '',
            count : '',
        }
    },
    methods : {
        want_restart : function() {
            // 다시 하고 싶다는 의사 전달 
            // 추후에 인원이 다 안차더라도 게임 로비로 이동됨
            // 로비로 이동될 떄 게임방 주인은 순위 가장 높은 사람으로 선정
            this.restart_member_count++;
            let send_data = {
                user_key : this.my_key,
            };
            this.cancel_restart_val = true;
            socket.emit("restart_checking", send_data);
        },
        close_game : function() {
            // 서버에서 나가지고 첫번째 페이지로 이동됨
            socket.emit("out_game");
            this.$router.push({
                name : 'first_page',
            });
        },
        cancel_restart : function() {
            let send_data = {
                user_key : this.my_key,
            };
            socket.emit("restart_cancel", send_data);
        },
    }
}
