const select_user_mount = {
    template: `
        <div id="select_user_mount">
            <p> 몇 명이서 하나요?</p>
            <select v-model="selected">
                <option v-for="board in list" :value="board.val">{{board.selected}}</option>
            </select>
            <div></div>
            <button class="start_game" v-on:click="create_lobby">게임 생성</button>
        </div>
    `,
    data(){
        return {
            selected : '2',
            list : [
                {selected: '2명', val: 2},
                {selected: '3명', val: 3},
                {selected: '4명', val: 4}
            ]
        }
    },
    methods : {
        create_lobby : function(){
            console.log("click " + this.selected);
        }
    }
}

const first_page = {
    el : '#press_start',
    components: {
        'select_user_mount' : select_user_mount
    },
    data : {
        open_toggle: false,
        toggle_name : '게임 열기',
        is_show : 'inline',
    },
    methods : {
        toggle_open_game: function () {
            this.is_show = this.is_show === 'inline' ? 'none' : 'inline';
            this.toggle_name = this.toggle_name === '게임 열기' ? '뒤로 가기' : '게임 열기';
            return this.open_toggle = this.open_toggle === false ? true : false;
        }
    }
}
