import join_server from '@/components/join_server.vue'
import { io } from 'socket.io-client';

// 다른 곳에서도 쓸 수 있게 전역 선언
var socket;

export default {
    name : 'first_page',
    components : {
        join_server : join_server,
    },
    mounted() {
        socket.on("enter_room_success", (data)=> {
            // router로 방 선택/ 생성 창으로 이동
            console.log(data);
            
        });

    },
    created() {
        
    },
    data () {
        return {
            is_join_server : false,
            loading : false,
            name : '',
        }
    },
    methods : {
        join_server: function () {
            this.is_join_server = true;
        },

        loading_start : function(data) {
            this.is_join_server = false;
            this.loading = true;
            this.name = data.name;
            
            socket = io('http://' + data.ip + ":3000");

            socket.emit("enter_room_gate", this.name);
        }
    }
}
