import { connect, io } from 'socket.io-client';
import { default as Create_lobby } from '@/components/create_lobby.vue';

// 다른 곳에서도 쓸 수 있게 전역 선언
var socket;

export default {
    name : 'room_list',
    components : {
        Create_lobby : Create_lobby,
    },
    async created() {
        socket = await this.connect();
        console.log(socket);
        if( socket.id == undefined || socket.id == '' ){
            // 접속 실패로 이전화면으로 돌림
            alert("접속에 실패하였습니다.\n 주소를 다시 확인해주세요");
            this.$router.push({
                name: 'first_page',
            });
        }
        else {
            this.enter_server();
        }
        
    },
    mounted() {
        socket.on("enter_room_success", (data)=> {
            // router로 방 선택/ 생성 창으로 이동
            console.log(data);
            
            this.room_list = data;
        });
    },
    data () {
        return {
            name : this.$route.params.name,
            room_list : '',
            listing : true,
        }
    },
    methods : {
        // 소켓 연결 비동기 처리
        connect : function(){
            return new Promise( function(resolve) {
                setTimeout(function() {
                    socket = io('http://' + this.$route.params.ip + ":3000");
                    resolve(socket)
                  }, 3000);
            });
        },

        enter_server : function() {
            socket.emit("enter_room_gate", this.$route.params.name);
        },

        create_room : function() {
            this.listing = false;
        },
        back_page : function() {
            this.listing = true;
        }
    },
}

export { socket };
