            // 선택되지 못한 보드가 눌렸을 경우 현재 선택된 보드만 누르게 경고를 띄워야함

        });
        
        //보드 진행 시 순서 관련 변수 업데이트
        socket.on("change_board_order_val", (data) => {
            console.log(data);
            this.board_order = data.board_order;
            this.board_cube_order = data.board_cube_order;
        });

