                alert("아직 재료를 고를 수 없습니다.")
                return;
            }
            // 재룤 카드 선택 이벤트는 무조건 0번에 존재하므로 
            if( this.final_round_order[this.board_order].user_cube_data[0][this.board_cube_order].is_select == true
                && this.final_round_order[this.board_order].user_key == this.my_key )
