export default {
  name: 'game_lobby_master',

  created() {
    var server_app = require('express')();

    var server = require('http').createServer(server_app); // http server를 socket.io server로 upgrade한다


    var io = require('socket.io')(server);

    server_app.get('/', function (req, res) {
      res.sendFile(__dirname + '/index.html');
    });
    io.on('connection', function (socket) {
      socket.on('login', function (data) {
        console.log(data);
      });
    });
  },

  methods: {}
};
