        socket.on("chat", (data) => {
            // console.log("서버에서 받아온 데이터");
            // console.log(data);
            // console.log("받아오기 끝");
            switch ( data.type ) {
                case "announce" :
                    this.textarea += data.speaker + " 이(가) " + data.msg + "\n";
                    break;
                case "normal" :
                    this.textarea += data.speaker + " : " + data.msg + "\n";
                    break;
            }
            //console.log("chat :: ");
            //console.log(data);
        });

        // 선택할 수 있는 카드 공개
        socket.on("ingredient_select_card_open", (data) => {
            //console.log(data);
            this.ingredient_card_selected.card_1 = data[0];
            this.ingredient_card_selected.card_2 = data[1];
            this.ingredient_card_selected.card_3 = data[2];
            this.ingredient_card_selected.card_4 = data[3];
            this.ingredient_card_selected.card_5 = data[4];
        });

        // 인게임 초기 셋팅값을 전송 받는 함수
        socket.on("draw_ingredient_card_result", (data) => {
            this.user_data = data;
            //console.log(this.user_data);
        });

        // 게임 순서 정하는 순서를 받는 함수
        socket.on("round_order_setting_before" , (data) => {
            //console.log(data);
            let as = [];

            for( let i = 0; i < data.length; i++) {
                for( let j = 0; j < this.user_data.length; j++){
                    if( data[i] == this.user_data[j].user_key ){

                        let choice_data = {
                            user_key  : this.user_data[j].user_key,
                            user_name : this.user_data[j].user_name,
                            is_select : false
                        }
                        
                        as.push(choice_data);
                        
                        choice_data = '';
                    }
                }
            }
            this.round_order = as;
            //console.log(this.round_order[this.round_order_selecter].user_key);
        });


        socket.on("select_round_order_recive", (data) => {
            let btn_ele = '';
            switch(data.order) {
                case "1" : 
                    btn_ele = this.$refs.btn_1;
                    break;
                case "2" :
                    btn_ele = this.$refs.btn_2;
                    break;
                case "3" :
                    btn_ele = this.$refs.btn_3;
                    break;
                case "4" : 
                    btn_ele = this.$refs.btn_4;
                    break;
                case "5" :
                    btn_ele = this.$refs.btn_5;
                    break;
                case "6" :
                    btn_ele = this.$refs.btn_6;
                    break;
                case "7" :
                    btn_ele = this.$refs.btn_7;
                    break;
                case "8" :
                    btn_ele = this.$refs.btn_7;
                    break;
                default :
                    btn_ele = '';
                    break;
            }
            
            if( btn_ele != '' ){
                for( let i = 0; i < this.user_data.length; i++){
