                        }
                    }
                }
            }
            else {

            }
            
            console.log(data);
        });

    },
    components : {
        modal_component : modal_area,
        personal_game_data : personal_game_data,
    
    },
    data() {
        return  {
            user_data : '', 
            my_key : socket.id,
            my_name : '',
            my_color : '',
            room_owner : '',
            total_dicount_adventruer : 4,
            textarea : '',
            message : '',
            ingredient_card_selected : {
                card_1 : '0',
                card_2 : '0',
                card_3 : '0',
                card_4 : '0',
                card_5 : '0',
            },
            decide_order : false,
            round_order : '',
            round_order_selecter : 0,
        }
    },

    methods : {
        use_chat : function(){
            if( this.message !== '' ){
              let msg = {
                speaker : this.my_name,
                msg : this.message,
                type : "normal"
              }
            //   console.log("서버에 보낼 데이터");
            //   console.log(msg);
            //   console.log("보낼준비 끝");
              this.message = '';
              socket.emit("chat", msg);
            }
        },

        draw_ingredient_card : function(cnt) {
            let data = {
                cnt : cnt,
                my_key : this.my_key,
            }
            socket.emit("draw_ingredient_card", data);
        },

        round_ready : function() {
            console.log("ready");
        },

        decide_order_end : function() {
            this.decide_order = true;
        },

        select_order_btn : function(val, e){
            // 선택을 처음 하는 경우 클래스에 반영
            if ( !e.target.className.includes(this.my_color) ){
                e.target.className += ' ' + this.my_color;
            }
            // 같은 버튼을 선택했을 경우 클래스에서 제거
            else {
                let point = e.target.className.indexOf(this.my_color);
                let new_name = e.target.className.substring(0, point);

                e.target.className = new_name;
            }
            let round_order_data = {
                user_key : this.my_key,
                order : val
            };

            // 버튼 선택 결과 전송
            socket.on("select_round_order", round_order_data);
        }
    }
}
